function drawStatistic(e,t,a){function i(){var i=google.visualization.arrayToDataTable(t),o={title:e,pieHole:.4},c=new google.visualization.PieChart(document.getElementById(a));c.draw(i,o)}google.charts.load("current",{packages:["corechart"]}),google.charts.setOnLoadCallback(i)}$(document).ready(function(){$.ajax({type:"GET",url:"/master/devices-statistics/values",success:function(e){if(e[0].length>0){var t=[["Name","Value"]];for(i=0;i<e[0].length;i++)t.push([e[0][i].device_type,parseInt(e[0][i].count)]);$("#donutchart-device").fadeIn(500),drawStatistic("Devices type statistics",t,"donutchart-device")}else $("#donutchart-device-none").text("No devices types");if(e[1].length>0){var a=[["Name","Value"]];for(i=0;i<e[1].length;i++)a.push([e[1][i].device_os,parseInt(e[1][i].count)]);$("#donutchart-os").fadeIn(500),drawStatistic("Devices OS statistics",a,"donutchart-os")}else $("#donutchart-os-none").text("No devices OS")}})}),$(window).on("load",function(){});