function showPosition(o){var t=$(".geolocation-token").find("input").val();$.ajax({type:"POST",url:"/send-location",data:{_token:t,user_location_lat:o.coords.latitude,user_location_lng:o.coords.longitude}})}$(document).ready(function(){$('[data-toggle="tooltip"]').tooltip(),$(".user-name span").click(function(){$(this).closest(".user-name").hide(500,function(){$(".get-user-name").show(500)})}),$(".arrow-up").click(function(){$("html, body").animate({scrollTop:0})}),$(".arrow-down").click(function(){$("html, body").animate({scrollTop:$(document).height()})})}),$(window).on("load",function(){"geolocation"in navigator?navigator.geolocation.getCurrentPosition(showPosition):console.log("Geolocation is not supported by this browser")});